package Chap2;

public class SumDiff1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(82+17);
		System.out.println(82-17);
		
		// 문자열을 제거하여 출력하기 
		System.out.println("82+17=" + 82+17);
		
		//문자열을 +로 인식하지 않도록 괄호로 묶어서 출력하기 
		System.out.println("82+17=" + (82+17));
	}

}
